function authFunction(uname, password, $http, $rootScope, success, error) {
  
var authSuccess = success;
    var authError = error;
    //Moodle authentication

    $rootScope.loginToBB = function(successcb, errorcb) {
        var url = "https://kryptosmw.kryptosmobile.com/webadapter3/services/authenticate/LIMESTONE/BB";
        var data = {};

        if (window.device) {
            data = "username=" + uname + "&password=" + password;
        } else {
            url = "/websimulator/json?url=" + url;
            data = {
                method: "POST",
                body: "username=" + uname + "&password=" + password
            };
        }
        $.blockUI({
            message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div><h3 style="color:#fff;">Authenticating</h3></div>'
        });

        $http.post(url, data).success(function(data, status, headers, config) {
            $.unblockUI();
            $rootScope.mticket = data.ticket;
            if (successcb) {
                $rootScope.bbticket = data.ticket;
                $rootScope.isLoggedInToBB = true;
            }
        }).error(function(data, status, headers, config) {
            $.unblockUI();
            if (errorcb) {
                errorcb(data);
            }
        });

    };



    $rootScope.invokeBBAPI = function(endpoint, postdata, callback) {
        $.blockUI();
        var serviceurl = endpoint + "?ticket=" + $rootScope.bbticket;
        var url = "";
        var proxyMethod = "POST";
        var proxyData = postdata;
        if (window.device) {
            url = serviceurl;
        } else {
            url = "/websimulator/json?url=" + serviceurl;
            proxyMethod = "POST";
            proxyData = {
                method: "POST",
                body: postdata
            }
        }
        $http({
            method: proxyMethod,
            url: url,
            data: proxyData
        }).
        success(function(data, status, headers, config) {
            $.unblockUI();
            callback(data, status, headers, config);
        }).
        error(function(data, status, headers, config) {
            $.unblockUI();
            //alert("Error in service call : " + data + " Status : " + status);
            if (status == 403) { //Ticket expired.. need to relogin
                var authUrl = "https://kryptosmw.kryptosmobile.com/webadapter3/services/authenticate/LIMESTONE/BB";
                //var uname = $.jStorage.get('bbusername');
                //var password = $.jStorage.get('bbpassword');
                var authData = {};
                if (window.device) {
                    authData = "username=" + uname + "&password=" + password;
                } else {
                    authUrl = "/websimulator/json?url=" + authUrl;
                    authData = {
                        method: "POST",
                        body: "username=" + uname + "&password=" + password
                    };
                }
                $http.post(authUrl, authData).success(function(data, status, headers, config) {
                    if (data.ticket) {
                        $rootScope.bbticket = data.ticket;
                        $rootScope.invokeBBAPI(endpoint, postdata, callback);
                    }
                }).error(function(data, status, headers, config) {
                    //authError(data);
                });
            }

        });


    };

    $rootScope.mgetAPI = function(endpoint, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        //authSuccess(data);
                        $rootScope.mticket = data.ticket;
                        $rootScope.mgetAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };




    //Portal auth
    var pcUrl = "https://kryptosmw.kryptosmobile.com/webadapter3/services/authenticate/LIMESTONE/SIS";
    var data = {};
    if (window.device) {
        data = "username=" + uname + "&password=" + password;
    } else {
        pcUrl = "/websimulator/json?url=" + pcUrl;
        data = {
            method: "POST",
            body: "username=" + uname + "&password=" + password
        };
    }
    var authUrl = pcUrl;
    var authData = data;
    $rootScope.generateST = function(tgt, service, callback) {
        //Bypassing the ST as there is no service ticket required here..
        callback(tgt);
    };

    $rootScope.postAPI = function(endpoint, postdata, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "POST";
            var proxyData = postdata;
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + serviceurl;
                proxyMethod = "POST";
                proxyData = {
                    method: "POST",
                    body: postdata
                }
            }
            $http({
                method: proxyMethod,
                url: url,
                data: proxyData
            }).
            success(function(data, status, headers, config) {
                $.unblockUI();
                callback(data, status, headers, config);
            }).
            error(function(data, status, headers, config) {
                $.unblockUI();
                //alert("Error in service call : " + data + " Status : " + status);
                if (status == 403) { //Ticket expired.. need to relogin
                    $http.post(authUrl, authData).success(function(data, status, headers, config) {
                        authSuccess(data);
                        $rootScope.getAPI(endpoint, callback);
                    }).error(function(data, status, headers, config) {
                        authError(data);

                    });
                }

            });

        });

    };



    $rootScope.getAPI = function(endpoint, callback) {
        $.blockUI();
        $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
            var serviceurl = endpoint + "?ticket=" + st;
            var url = "";
            var proxyMethod = "GET";
            var proxyData = {};
            if (window.device) {
                url = serviceurl;
            } else {
                url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
                proxyMethod = "POST";
                proxyData = {
                    method: "GET"
                }
            }
            $http({
                method: proxyMethod,
